import React, { useState, useEffect } from "react";
import { FaFilter } from "react-icons/fa";
import CourseCard from "./CourseCard"; // Make sure this component exists

// Example constant courses array
const allCoursesData = [
  { id: 1, title: "Cyber Security Basics", price: 50, rating: 4.5, topic: "Cyber Security" },
  { id: 2, title: "Intro to AI", price: 40, rating: 4.7, topic: "AI" },
  { id: 3, title: "Web Dev Bootcamp", price: 30, rating: 4.2, topic: "Web Development" },
  { id: 4, title: "Machine Learning 101", price: 60, rating: 4.8, topic: "Machine Learning" },
  { id: 5, title: "Data Science Fundamentals", price: 45, rating: 4.6, topic: "Data Science" },
];

function Allcourses() {
  const [sortOption, setSortOption] = useState("");
  const [topicFilter, setTopicFilter] = useState("");
  const [filteredAndSortedCourses, setFilteredAndSortedCourses] = useState(allCoursesData);

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const handleTopicFilterChange = (e) => {
    setTopicFilter(e.target.value);
  };

  useEffect(() => {
    let updatedCourses = [...allCoursesData];

    // Apply topic filter
    if (topicFilter) {
      updatedCourses = updatedCourses.filter(course => course.topic === topicFilter);
    }

    // Apply sorting
    if (sortOption === "price") {
      updatedCourses.sort((a, b) => a.price - b.price);
    } else if (sortOption === "price-desc") {
      updatedCourses.sort((a, b) => b.price - a.price);
    } else if (sortOption === "rating") {
      updatedCourses.sort((a, b) => b.rating - a.rating);
    }

    setFilteredAndSortedCourses(updatedCourses);
  }, [sortOption, topicFilter]);

  return (
    <div className="app">
      <header className="header">
        <h1 className="headerTitle">All Courses</h1>
      </header>

      <div className="content">
        {/* Left Panel */}
        <aside className="sidebar">
          <h2 className="filterBox">
            <FaFilter className="filterIcon" /> Filter & Sort
          </h2>

          <select
            className="sortDropdown"
            value={sortOption}
            onChange={handleSortChange}
          >
            <option value="">Sort by</option>
            <option value="price">Price (Low to High)</option>
            <option value="price-desc">Price (High to Low)</option>
            <option value="rating">Rating (High to Low)</option>
          </select>

          <div className="filterSection">
            <h3 className="filterHeading">Filter by Topic</h3>
            <div className="filterOptions">
              {["Cyber Security", "AI", "Web Development", "Machine Learning", "Data Science"].map(topic => (
                <label key={topic} className="filterOption">
                  <input
                    type="radio"
                    name="topic"
                    value={topic}
                    onChange={handleTopicFilterChange}
                    checked={topicFilter === topic}
                  />
                  {topic}
                </label>
              ))}
            </div>
          </div>
        </aside>

        {/* Right Panel */}
        <div className="courseList">
          {filteredAndSortedCourses.map(course => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Allcourses;
