// index.js
import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import Homepage from "./Component/Homepage/Homepage";
import Course from "./Component/Course/courseList";
import Job from "./Component/Jobs/jobs";
import Navbar from "./Component/Navbar/navbar";
import Footer from "./Component/Footer/footer";
import reportWebVitals from "./reportWebVitals";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Simple LoginModal
function LoginModal({ onClose }) {
  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h2>Login</h2>
        {/* You can replace this with a real login form */}
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
}
<nav className="navbar">
      <div className="logo-container">
        <svg
          className="logo-icon"
          viewBox="0 0 24 24"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M20 7L12 3L4 7M20 7L12 11M20 7V17L12 21M12 11L4 7M12 11V21M4 7V17L12 21"
            stroke="#2563eb"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
        <h1 className="site-title">PathX</h1>
      </div>

      <button className="login-btn" onClick={() => setAuthOpen(true)}>
        Login
      </button>
    </nav>
function App() {
  const [authOpen, setAuthOpen] = useState(false);

  return (
    <Router>
      {/* Navbar receives setAuthOpen */}
      <Navbar setAuthOpen={setAuthOpen} />

      {/* Show login modal if authOpen is true */}
      {authOpen && <LoginModal onClose={() => setAuthOpen(false)} />}

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Homepage />} />
        <Route path="/courses" element={<Course />} />
        <Route path="/jobs" element={<Job />} />
      </Routes>

      <Footer />
    </Router>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

reportWebVitals();
