import React, { useState } from "react";
import "./JobsPage.css";

export default function JobsPage() {
  const jobsData = [
    {
      title: "Frontend Developer Intern",
      company: "TechNova",
      location: "Remote",
      skills: ["React", "CSS", "JavaScript"],
      experience: "Beginner",
      type: "Internship",
      image: "https://via.placeholder.com/250x150?text=Frontend+Intern",
      description:
        "Assist in building responsive web interfaces using React and TailwindCSS.",
    },
    {
      title: "Data Science Trainee",
      company: "DataVerse",
      location: "Dhaka",
      skills: ["Python", "Pandas", "Machine Learning"],
      experience: "Entry-Level",
      type: "Internship",
      image: "https://via.placeholder.com/250x150?text=Data+Science",
      description:
        "Work with real datasets, perform data cleaning and implement ML algorithms.",
    },
    // add more jobs...
  ];

  const [filter, setFilter] = useState({ type: "All", location: "All" });
  const [hovered, setHovered] = useState(null);
  const [selectedJob, setSelectedJob] = useState(null);

  const uniqueTypes = ["All", ...new Set(jobsData.map((j) => j.type))];
  const uniqueLocations = ["All", ...new Set(jobsData.map((j) => j.location))];

  const filteredJobs = jobsData.filter((job) => {
    const typeMatch = filter.type === "All" || job.type === filter.type;
    const locationMatch =
      filter.location === "All" || job.location === filter.location;
    return typeMatch && locationMatch;
  });

  return (
    <div className="jobs-page">
      <h1 className="title">Job & Opportunities</h1>

      <div className="filters">
        <select onChange={(e) => setFilter({ ...filter, type: e.target.value })}>
          {uniqueTypes.map((t) => (
            <option key={t}>{t}</option>
          ))}
        </select>

        <select
          onChange={(e) => setFilter({ ...filter, location: e.target.value })}
        >
          {uniqueLocations.map((l) => (
            <option key={l}>{l}</option>
          ))}
        </select>
      </div>

      <div className="jobs-grid">
        {filteredJobs.map((job, i) => (
          <div
            key={i}
            className="job-card"
            onMouseEnter={() => setHovered(i)}
            onMouseLeave={() => setHovered(null)}
          >
            <img src={job.image} alt={job.title} className="job-image" />

            {hovered === i ? (
              <div className="job-hover">
                <h3>{job.title}</h3>
                <p><b>Company:</b> {job.company}</p>
                <p><b>Location:</b> {job.location}</p>
                <p><b>Skills:</b> {job.skills.join(", ")}</p>
                <p><b>Experience:</b> {job.experience}</p>
                <p><b>Type:</b> {job.type}</p>
                <button
                  className="job-btn"
                  onClick={() => setSelectedJob(job)}
                >
                  View Details
                </button>
              </div>
            ) : (
              <div className="job-basic">
                <h3>{job.title}</h3>
                <p>{job.company}</p>
                <p>{job.location}</p>
              </div>
            )}
          </div>
        ))}
      </div>

      {selectedJob && (
        <div className="modal-overlay" onClick={() => setSelectedJob(null)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedJob.image}
              alt={selectedJob.title}
              style={{ width: "100%", borderRadius: "8px" }}
            />
            <h2>{selectedJob.title}</h2>
            <p><b>Company:</b> {selectedJob.company}</p>
            <p><b>Location:</b> {selectedJob.location}</p>
            <p><b>Type:</b> {selectedJob.type}</p>
            <p><b>Experience:</b> {selectedJob.experience}</p>
            <p><b>Skills:</b> {selectedJob.skills.join(", ")}</p>
            <p style={{ marginTop: "10px" }}>{selectedJob.description}</p>
            <button
              className="close-btn"
              onClick={() => setSelectedJob(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
