const defaultImage = "/Course-Images/Web Development For Beginners 2024.png";

function CourseCard({ course }) {
  const [isHovered, setIsHovered] = useState(false);

  // Get the image for the course, or fall back to the default image
  const courseImage = courseImages[course.title] || defaultImage;

  // Truncate the course description to a specified length
  const truncateText = (text, maxLength) => {
    if (text.length > maxLength) {
      return `${text.substring(0, maxLength)}...`;
    }
    return text;
  };

  const shortDescription = truncateText(course.description, 300);

  return (
    <Link
      to={`/courses-ml1/${course.courseId}`}
      style={{ textDecoration: "none" }}
    >
      <div
        style={{
          ...styles.courseCard,
          ...(isHovered && styles.courseCardHover),
        }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <img src={courseImage} alt={course.title} style={styles.courseImage} />
        <div style={styles.courseInfo}>
          <h2 style={styles.courseTitle}>{course.title}</h2>
          <p style={styles.courseDescription}>{shortDescription}</p>
          <p style={styles.instructor}>{course.instructorName}</p>
         
          <p style={styles.courseDetails}>
            {course.duration} Total Duration • {course.moduleCount} lectures •{" "}
            {course.status}
          </p>
          <p>Approved By: {course.moderatorName}</p>
          <div style={styles.price}>
            <span style={styles.discountedPrice}>${course.price}</span>
            {/* <span style={styles.originalPrice}>Tk.{course.originalPrice}</span> */}
          </div>
        </div>
      </div>
    </Link>
  );
}

function Allcourses() {
  const [allCourses, setAllCourses] = useState([]);
  const [sortOption, setSortOption] = useState("");
  const [ratingFilter, setRatingFilter] = useState("");
  const [topicFilter, setTopicFilter] = useState("");
  const [filteredAndSortedCourses, setFilteredAndSortedCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        // Send the fetch request with necessary headers and credentials
        const response = await fetch(`http://localhost:8080/api/courses`, {
          method: "GET", // Explicitly set the GET method
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include", // This ensures cookies are included with the request
          // Query parameters are included in the URL itself
          // You can add parameters like ?page=0&size=10&sortBy=title&sortOrder=asc
        });

        // Log the response status to diagnose redirect or auth issues
        console.log("Response Status:", response.status);

        // Check if the response is okay (status 200-299)
        if (!response.ok) {
          // If the status is 401 or 302, it's likely an auth issue
          if (response.status === 401 || response.status === 302) {
            console.error("Authentication required. Redirecting to login.");
            return;
          }
          console.error("Failed to fetch courses. Status:", response.status);
          return;
        }

        const data = await response.json();
        // Log the fetched data to the console
        console.log("Fetched Courses:", data);

        setAllCourses(data); // Store all courses here
        setFilteredAndSortedCourses(data); // Initialize filtered and sorted courses
        setLoading(false);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    fetchCourses();
  }, [sortOption]);

  const handleTopicFilterChange = (event) => {
    setTopicFilter(event.target.value);
  };

  // Function to handle sort changes
  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  // Function to handle rating filter changes
  const handleRatingFilterChange = (event) => {
    setRatingFilter(event.target.value);
  };

  useEffect(() => {
    let updatedCourses = [...allCourses];

    console.log("Before filtering:", updatedCourses); // Log before any filtering

    // Apply topic filter
    if (topicFilter) {
      updatedCourses = updatedCourses.filter(
        (course) => course.field === topicFilter
      );
    }

    console.log("After topic filtering:", updatedCourses); // Log after topic filtering

    // Apply sorting by price first (Low to High)
    if (sortOption === "price") {
      updatedCourses.sort((a, b) => a.price - b.price);
    } else if (sortOption === "price-desc") {
      updatedCourses.sort((a, b) => b.price - a.price);
    }
    // Apply sorting by rating (High to Low)
    else if (sortOption === "rating") {
      updatedCourses.sort((a, b) => b.rating - a.rating); /// Correct descending sort for decimals
    }
    console.log("After sorting:", updatedCourses);

    setFilteredAndSortedCourses(updatedCourses);
  }, [allCourses, sortOption, ratingFilter, topicFilter]); // Re-run effect when either sortOption or ratingFilter changes

  /* ================================= This is My Main Portion Showing Courses ================================= */
  return (
    <div style={styles.app}>
      <header style={styles.header}>
        <h1 style={styles.headerTitle}>All Courses</h1>
      </header>
      <div style={styles.content}>
        {/* Left Panel for Filters and Sorting */}
        <aside style={styles.sidebar}>
          <h2 style={styles.sidebarTitle}>Filter & Sort</h2>
          <select
            style={styles.sortDropdown}
            value={sortOption}
            onChange={handleSortChange}
          >
            <option value="">Sort by</option>
            <option value="price">Price (Low to High)</option>
            <option value="price-desc">Price (High to Low)</option>
            {/* <option value="rating">Rating (High to Low)</option> */}
          </select>
          <h2 style={styles.filterBox}>
            <FaFilter style={styles.filterIcon} />
            Filter Options
          </h2>

          
          <div style={styles.filterSection}>
            <h3 style={styles.filterHeading}>Filter by Topic</h3>
            <div style={styles.filterOptions}>
              <label style={styles.filterOption}>
                <input
                  type="radio"
                  name="topic"
                  value="Cyber Security"
                  onChange={handleTopicFilterChange}
                  checked={topicFilter === "Cyber Security"}
                />
                Cyber Security
              </label>
              <label style={styles.filterOption}>
                <input
                  type="radio"
                  name="topic"
                  value="AI"
                  onChange={handleTopicFilterChange}
                  checked={topicFilter === "AI"}
                />
                AI
              </label>
              <label style={styles.filterOption}>
                <input
                  type="radio"
                  name="topic"
                  value="Web Development"
                  onChange={handleTopicFilterChange}
                  checked={topicFilter === "Web Development"}
                />
                Web Development
              </label>
              <label style={styles.filterOption}>
                <input
                  type="radio"
                  name="topic"
                  value="Machine Learning"
                  onChange={handleTopicFilterChange}
                  checked={topicFilter === "Machine Learning"}
                />
                Machine Learning
              </label>
              <label style={styles.filterOption}>
                <input
                  type="radio"
                  name="topic"
                  value="Data Science"
                  onChange={handleTopicFilterChange}
                  checked={topicFilter === "Data Science"}
                />
                Data Science
              </label>
            </div>
          </div>
        </aside>

        {/* Right Panel for Course Cards */}
        <div style={styles.courseList}>
          {filteredAndSortedCourses.map((course) => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      </div>
    </div>
  );
}

