import React, { useState, useEffect } from "react";
import { FaFilter } from "react-icons/fa";

// Example constant courses array (replace with your actual data)
const allCourses = [
  {
    id: 1,
    title: "The Complete Machine Learning Bootcamp 2024",
    description:
      "Master Machine Learning with Python, TensorFlow, and Scikit-Learn. Build real-world ML applications and models.",
    instructor: "Dr. John Doe",
    rating: 4.8,
    price: 2499.99,
    topic: "Machine Learning",
  },
  {
    id: 2,
    title: "Cybersecurity Essentials: Protect Your Digital World",
    description:
      "Learn the fundamentals of cybersecurity, ethical hacking, and protecting sensitive data in a digital landscape.",
    instructor: "Dr. Emily Clarke",
    rating: 4.5,
    price: 1499.99,
    topic: "Cyber Security",
  },
  {
    id: 3,
    title: "Advanced Web Development Bootcamp 2024",
    description:
      "Become a full-stack web developer with in-depth training on modern frameworks like React, Node.js, and Express.",
    instructor: "Mr. Alex Turner",
    rating: 4.8,
    price: 2499.99,
    topic: "Web Development",
  },
  {
    id: 4,
    title: "AI for Everyone: Understanding Artificial Intelligence",
    description:
      "An introduction to AI concepts, applications, and implications for professionals across all industries.",
    instructor: "Dr. Sophia Wang",
    rating: 4.9,
    price: 599.99,
    topic: "AI",
  },
  // Add more courses as needed
];

// Simple frontend CourseCard
function CourseCard({ course }) {
  return (
    <div className="courseCard">
      <div className="courseImage"></div>
      <div className="courseInfo">
        <h2 className="courseTitle">{course.title}</h2>
        <p className="courseDescription">{course.description}</p>
        <p className="instructor">{course.instructor}</p>
        <p className="courseDetails">Rating: {course.rating}</p>
        <p className="price">${course.price}</p>
      </div>
    </div>
  );
}

function Allcourses() {
  const [sortOption, setSortOption] = useState("");
  const [topicFilter, setTopicFilter] = useState("");
  const [filteredAndSortedCourses, setFilteredAndSortedCourses] =
    useState(allCourses);

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const handleTopicFilterChange = (event) => {
    setTopicFilter(event.target.value);
  };

  useEffect(() => {
    let updatedCourses = [...allCourses];

    // Apply topic filter
    if (topicFilter) {
      updatedCourses = updatedCourses.filter(
        (course) => course.topic === topicFilter
      );
    }

    // Apply sorting
    if (sortOption === "price") {
      updatedCourses.sort((a, b) => a.price - b.price);
    } else if (sortOption === "price-desc") {
      updatedCourses.sort((a, b) => b.price - a.price);
    } else if (sortOption === "rating") {
      updatedCourses.sort((a, b) => b.rating - a.rating);
    }

    setFilteredAndSortedCourses(updatedCourses);
  }, [sortOption, topicFilter]);

  return (
    <div className="app">
      <header className="header">
        <h1 className="headerTitle">All Courses</h1>
      </header>
      <div className="content">
        {/* Left Panel */}
        <aside className="sidebar">
          <h2 className="filterBox">
            <FaFilter className="filterIcon" /> Filter Options
          </h2>

          <select
            className="sortDropdown"
            value={sortOption}
            onChange={handleSortChange}
          >
            <option value="">Sort by</option>
            <option value="price">Price (Low to High)</option>
            <option value="price-desc">Price (High to Low)</option>
            <option value="rating">Rating (High to Low)</option>
          </select>

          <div className="filterSection">
            <h3 className="filterHeading">Filter by Topic</h3>
            <div className="filterOptions">
              {["Cyber Security", "AI", "Web Development", "Machine Learning", "Data Science"].map(
                (topic) => (
                  <label key={topic} className="filterOption">
                    <input
                      type="radio"
                      name="topic"
                      value={topic}
                      onChange={handleTopicFilterChange}
                      checked={topicFilter === topic}
                    />
                    {topic}
                  </label>
                )
              )}
            </div>
          </div>
        </aside>

        {/* Right Panel */}
        <div className="courseList">
          {filteredAndSortedCourses.map((course) => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Allcourses;
